using System.Collections.Generic; public class LoanManager { public List<Loan> Loans = new(); public bool LoanBook(Book book, Member member) { if (!book.Available) return false; Loans.Add(new Loan(book, member)); book.Available = false; return true; } public bool ReturnBook(Book book) { var loan = Loans.Find(l => l.Book == book); if (loan != null) { Loans.Remove(loan); book.Available = true; return true; } return false; } }